import swCharacters from "../../data/characters.json";
import swPlanets from "../../data/planets.json";
import swStarships from "../../data/starships.json";

import { useState } from "react";

export const Content = () => {

   const [selector, setSelector] = useState("Character"); //initial value : Character

   const handleSelectorChange = (event) => {
      // console.log(event.target.value)
      setSelector(event.target.value);
   }

   return (
      <>
      <div className="content-layout">
        <select 
         value={selector}
         placeholder="Choose your path" 
         className="select-item"
         onChange={handleSelectorChange}>
          <option>Characters</option>
          <option>Planets</option>
          <option>Starships</option>
        </select>

        {/* non-HTML elements should be used in CURLY BRACES */}

        {selector==="Characters" && swCharacters.map( (character) => ( //here && means if left side is true, then show the right side
          <div key={character.name} className="item-list-layout">
          <div>
            <p>name: {character.name}</p>
            <p>gender: {character.gender}</p>
            <p>birthday: {character.birth_year}</p>
          </div>
          <div className="item-image-layout">
            <button className="favorite-button">I am your fan!!</button>
            <img
              className="item-image" src={character.url} alt="asd" />
          </div>
        </div>
        ))}
      </div>
      </>
   )
};
